mapper.select:
SELECT count(*) FROM `Project_User`
LEFT JOIN Project on Project_User.Project_ID=Project.Project_ID
WHERE Project_User.User_ID=#{0,jdbcType=VARCHAR} AND  Project.Is_Userful=1  AND Project_User.Type=#{1,jdbcType=VARCHAR} AND Project.Is_End=#{2,jdbcType=BIT}

SELECT Project.* FROM `Project_User`
LEFT JOIN Project on Project_User.Project_ID=Project.Project_ID
WHERE Project_User.User_ID=#{0,jdbcType=VARCHAR} AND Project_User.Type=#{1,jdbcType=VARCHAR} AND Project.Is_Userful=1
  AND Project.Is_Default = #{2,jdbcType=BIT}
LIMIT #{3} , #{4}
